import React, { useEffect, useState } from 'react';
import axios from '../../api/axios';

function Login({ onLoginExitoso }) {
  const [correo, setCorreo] = useState('');
  const [contraseña, setContraseña] = useState('');
  const [mensaje, setMensaje] = useState('');

  useEffect(() => {
    const correoGuardado = localStorage.getItem('correoTemp');
    const claveGuardada = localStorage.getItem('claveTemp');
    if (correoGuardado) setCorreo(correoGuardado);
    if (claveGuardada) setContraseña(claveGuardada);
  }, []);

  const manejarEnvio = async (e) => {
    e.preventDefault();
    try {
      const respuesta = await axios.post('/auth/login', {
        correo,
        contraseña
      });

      localStorage.setItem('token', respuesta.data.token);
      localStorage.removeItem('correoTemp');
      localStorage.removeItem('claveTemp');

      if (onLoginExitoso) onLoginExitoso();
    } catch (error) {
      setMensaje(error.response?.data?.mensaje || 'Error al iniciar sesión');
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '30px' }}>
      <h2>Procesando inicio de sesión...</h2>
      <form onSubmit={manejarEnvio}>
        <button type="submit" style={{ padding: '10px 20px' }}>Confirmar</button>
      </form>
      {mensaje && <p style={{ color: 'red' }}>{mensaje}</p>}
    </div>
  );
}

export default Login;
