import React, { useState } from 'react';
import axios from '../../api/axios';

function Login({ onLoginExitoso, onRegistro }) {
  const [correo, setCorreo] = useState('');
  const [contraseña, setContraseña] = useState('');
  const [mensaje, setMensaje] = useState('');

  const manejarLogin = async () => {
    try {
      const respuesta = await axios.post('/auth/login', { correo, contraseña });
      localStorage.setItem('token', respuesta.data.token);
      if (onLoginExitoso) onLoginExitoso(); // cambia a la pantalla de bienvenida
    } catch (error) {
      setMensaje(error.response?.data?.mensaje || 'Error al iniciar sesión');
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '30px' }}>
      <h2>Iniciar sesión</h2>

      <input
        type="email"
        placeholder="Correo"
        value={correo}
        onChange={(e) => setCorreo(e.target.value)}
        style={{ width: '250px', padding: '10px', marginBottom: '10px' }}
      />
      <br />

      <input
        type="password"
        placeholder="Contraseña"
        value={contraseña}
        onChange={(e) => setContraseña(e.target.value)}
        style={{ width: '250px', padding: '10px', marginBottom: '20px' }}
      />
      <br />

      <button onClick={manejarLogin} style={{ marginRight: '10px' }}>Iniciar</button>
      <button onClick={onRegistro}>Registro</button>

      {mensaje && <p style={{ color: 'red', marginTop: '15px' }}>{mensaje}</p>}
    </div>
  );
}

export default Login;
