import React, { useState } from 'react';
import axios from '../../api/axios';

const PantallaRegistro = ({ onRegistro }) => {
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [contrase√±a, setContrase√±a] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [exito, setExito] = useState(false); // üëà Nuevo estado para √©xito

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post('/auth/registro', { nombre, correo, contrase√±a });

      setExito(true);  // üëà Mostrar mensaje de √©xito
      setMensaje('¬°Usuario registrado exitosamente! ‚úÖ');

      // Despu√©s de 2 segundos, redirigir autom√°ticamente al login
      setTimeout(() => {
        if (onRegistro) onRegistro();
      }, 2000);

    } catch (error) {
      setExito(false); // üëà No es √©xito
      setMensaje(error.response?.data?.mensaje || 'Error al registrarse');
      console.error('‚ùå Error en el registro:', error);
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '30px' }}>
      <h2>Registro</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Tu nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          required
          style={{ display: 'block', marginBottom: '10px', width: '250px', padding: '10px' }}
        />

        <input
          type="email"
          placeholder="Correo electr√≥nico"
          value={correo}
          onChange={(e) => setCorreo(e.target.value)}
          required
          style={{ display: 'block', marginBottom: '10px', width: '250px', padding: '10px' }}
        />

        <input
          type="password"
          placeholder="Contrase√±a"
          value={contrase√±a}
          onChange={(e) => setContrase√±a(e.target.value)}
          required
          style={{ display: 'block', marginBottom: '20px', width: '250px', padding: '10px' }}
        />

        <button type="submit" style={{ padding: '10px 20px' }}>Registrarse</button>

        {/* Mensaje din√°mico */}
        {mensaje && (
          <p style={{
            color: exito ? 'green' : 'red',
            marginTop: '15px',
            fontWeight: 'bold'
          }}>
            {mensaje}
          </p>
        )}
      </form>
    </div>
  );
};

export default PantallaRegistro;
