import React, { useState } from 'react';
import PantallaInicio from './PantallaInicio';
import PantallaRegistro from './PantallaRegistro';
import PantallaServicios from './PantallaServicios';
import PantallaSolicitud from './PantallaSolicitud';
import PantallaPago from './PantallaPago';
import PantallaSeguimiento from './PantallaSeguimiento';
import PantallaFinal from './PantallaFinal';
import './App.css';

function App() {

  const [pantalla, setPantalla] = useState('inicio');


  const getClase = (nombrePantalla) => {
    return pantalla === nombrePantalla ? 'pantalla activa' : 'pantalla';
  };

  return (
    <div className="App">
      {/* Pantalla de Inicio */}
      <div className={getClase('inicio')}>
        <PantallaInicio 

          onContinuar={() => setPantalla('registro')} 
        />
      </div>

      {/* Pantalla de Registro */}
      <div className={getClase('registro')}>
        <PantallaRegistro 
          onContinuar={() => setPantalla('servicios')}
          onVolver={() => setPantalla('inicio')}
        />
      </div>

      {/* Pantalla de Servicios */}
      <div className={getClase('servicios')}>
        <PantallaServicios 
          onContinuar={() => setPantalla('solicitud')}
          onVolver={() => setPantalla('registro')}
        />
      </div>

      {/* Pantalla de Solicitud */}
      <div className={getClase('solicitud')}>
        <PantallaSolicitud 
          onContinuar={() => setPantalla('pago')}
          onVolver={() => setPantalla('servicios')}
        />
      </div>

      {/* Pantalla de Pago */}
      <div className={getClase('pago')}>
        <PantallaPago 
          onContinuar={() => setPantalla('seguimiento')}
          onVolver={() => setPantalla('solicitud')}
        />
      </div>

      {/* Pantalla de Seguimiento */}
      <div className={getClase('seguimiento')}>
        <PantallaSeguimiento 
          onContinuar={() => setPantalla('final')}
          onVolver={() => setPantalla('pago')}
        />
      </div>

      {/* Pantalla Final */}
      <div className={getClase('final')}>
        <PantallaFinal 
          onReiniciar={() => setPantalla('inicio')}
        />
      </div>
    </div>
  );
}

export default App;
